class LevusScroll{constructor(s){this.elements=document.querySelector(s),this.items=this.elements.querySelectorAll(".scroll-item"),this.wrapper=this.elements.querySelector(".levus-scroll"),this.left=this.elements.querySelector(".left"),this.right=this.elements.querySelector(".right"),this.sizes=[],this.width=1,this.zIndex=0}getElements(){return this.items.forEach(s=>{const t=s.cloneNode(!0);this.wrapper.append(t)}),this.items=this.elements.querySelectorAll(".scroll-item"),this.items}toSizes(){this.items.forEach((s,t)=>this.sizes.push(t*this.width-this.width))}arrows(){this.left.addEventListener("click",()=>{const s=this.sizes.pop();this.sizes.unshift(s),this.move()}),this.right.addEventListener("click",()=>{const s=this.sizes.shift();this.sizes.push(s),this.move()})}move(){this.wrapper.offsetWidth<400?(this.width=this.wrapper.offsetWidth,this.items.forEach((s,t)=>{s.style.width=`${this.width}px`,s.style.transform=`translateX(${this.sizes[t]*this.width}px)`,0===this.sizes[t]?this.zIndex=3:-1===this.sizes[t]||1===this.sizes[t]?this.zIndex=2:this.zIndex=0,s.style.zIndex=this.zIndex})):this.wrapper.offsetWidth<600?(this.width=this.wrapper.offsetWidth/2,this.items.forEach((s,t)=>{s.style.width=`${this.width}px`,s.style.transform=`translateX(${this.sizes[t]*this.width}px)`,0===this.sizes[t]||1===this.sizes[t]||2===this.sizes[t]?this.zIndex=3:this.zIndex=0,s.style.zIndex=this.zIndex})):this.wrapper.offsetWidth>599&&(this.width=this.wrapper.offsetWidth/3,this.items.forEach((s,t)=>{s.style.width=`${this.width}px`,s.style.transform=`translateX(${this.sizes[t]*this.width}px)`,1===this.sizes[t]?this.zIndex=3:0===this.sizes[t]||2===this.sizes[t]?this.zIndex=2:this.zIndex=0,s.style.zIndex=this.zIndex})),console.log("move()")}resize(){window.addEventListener("resize",this.move)}init(){this.getElements(),this.toSizes(),this.arrows(),this.resize(),this.move()}}

new LevusScroll(".levus-scroll-wrapper").init(),
new LevusScroll(".levus-scroll-wrapper-2").init();